import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Login.css'
import { ReactComponent as LoginCat } from '../icons/login-cat.svg'

function Login()
{
    const app_name = 'swiPet'
    function buildPath(route) {
        if (process.env.NODE_ENV === 'production') 
            {
                return 'https://' + app_name +  '.herokuapp.com/' + route;
            }
            else
            {        
                return 'http://localhost:3001/' + route;
            }
    }

    let loginName;
    let loginPassword;

    const [message,setMessage] = useState('');


    const doLogin = async event => 
    {
        event.preventDefault();

        var obj = { login: loginName.value, password: loginPassword.value };
        var js = JSON.stringify(obj);

        try {
            const response = await fetch(buildPath('api/login'),
                {method:'POST',body:js,headers:{'Content-Type': 'application/json'}});

            var res = JSON.parse(await response.text());

            if( res.id <= 0 )
            {
                setMessage('User/Password combination incorrect');
            }
            else
            {
                var user = {firstName:res.firstName,lastName:res.lastName,id:res.id};
                localStorage.setItem('user_data', JSON.stringify(user));

                setMessage('');
                window.location.href = '/profile';
            }
        }
        catch(e)
        {
            alert(e.toString());
            return;
        }    
    };

    return(
        <div id="loginDiv">
            <div id="login-text">
                <div id="login-header">
                    <LoginCat />
                    <span id="inner-title">Login</span><br />
                </div>

                <div id="username-box">
                    <span id="username-title">Username</span><br />
                    <input type="text" id="loginName" /><br />
                </div>

                <div id="password-box">
                <span id="password-title">Password</span><br />
                <input type="password" id="loginPassword" /><br />
                </div>

                <input type="submit" id="loginButton" class="buttons" value = "Login"
                onClick={doLogin} />
                <span id="loginResult"></span>

                <p><a href = '/forgotpassword'><span>Forgot your password?</span></a></p>

            </div>


        </div>
    );
};

export default Login;
